import React from 'react';
import { Card, CardImg, CardImgOverlay,
    CardTitle, Breadcrumb, BreadcrumbItem } from 'reactstrap';
import { Link } from 'react-router-dom';
import { Loading } from './LoadingComponent';
import { baseUrl } from '../shared/baseUrl';
import { PlayArrowSharp } from '@material-ui/icons';

function RenderApartmentsItem ({apartment}) {
    return (
        <Card>
            <Link to={`/apartments/${apartment.id}`} >
                <CardImg width="100%" src={baseUrl + apartment.image} alt={apartment.name} />
                <CardImgOverlay>
                    <CardTitle>{apartment.name}</CardTitle>
                </CardImgOverlay>
            </Link>
        </Card>
    );
}

const ApartmentList = (props) => {

    /*const apartmentList = props.apartments.apartments.map((apartment) => {
        return (
            <div className="col-12 col-md-5 m-1"  key={apartment.id}>
                <RenderApartmentsItem apartment={apartment} />
            </div>
        );

    });*/

    if (props.isLoading) {
        return(
            <div className="container">
                <div className="row">            
                    <Loading />
                </div>
            </div>
        );
    }
    else if (props.errMess) {
        return(
            <div className="container">
                <div className="row"> 
                    <div className="col-12">
                        <h4>{props.errMess}</h4>
                    </div>
                </div>
            </div>
        );
    }
    else if(props.apartment !=null){
        return (
            <div className="container">
                <div className="row">
                    <Breadcrumb>
                        <BreadcrumbItem><Link to={`/apartments`}>Apartments</Link></BreadcrumbItem>
                        <BreadcrumbItem active>ApartmentList</BreadcrumbItem>
                    </Breadcrumb>
                    <div className="col-12">
                        <h3>{props.apartment.name}</h3>
                        <hr/>
                    </div>                
                </div>
                <div className="row">
                <div className="col-12 col-md-5 m-1">
                    <RenderApartmentsItem apartment={props.apartment} />
                </div>
                </div>
            </div>
        );
    }
}

export default ApartmentList;
