import React from 'react';
import { Card, CardImg, CardImgOverlay,
    CardTitle, Breadcrumb, BreadcrumbItem } from 'reactstrap';
import { Link } from 'react-router-dom';
import { Loading } from './LoadingComponent';
import { baseUrl } from '../shared/baseUrl';


function RenderEquipments({equipments}) {
    const eqps = equipments.map((equipment) => { 
        return (
            <Card>
                    <CardImg width="100%" src={baseUrl + equipment.image} alt={equipment.name} />
                    <CardImgOverlay>
                        <CardTitle>{equipment.name}</CardTitle>
                    </CardImgOverlay>
            </Card>
        );        
    })

    if (equipments == null) {
        return <div></div>
    }
    return (
        <div>
            <h3>Equipment</h3>
                {eqps}
            <h>SAY HI</h>
        </div>
    );
}

const RoomEquipmentDetail = (props) => { 
    if (props.isLoading) {
        return(
            <div className="container">
                <div className="row">            
                    <Loading />
                </div>
            </div>
        );
    }
    else if (props.errMess) {
        return(
            <div className="container">
                <div className="row">            
                    <h4>{props.errMess}</h4>
                </div>
            </div>
        );
    }
    else if (props.room != null) {
        return (
            <div className="container">
            <div className="row">
                <div className="col-12">
                    <h3>{props.room.name}</h3>
                    <hr />
                </div>                
            </div>
            <div className="row">
                <div className="col-12 col-md-5 m-1">
                <RenderEquipments equipments={props.equipments}
                />
                </div>
            </div>
            </div>
        );
    }
}


export default RoomEquipmentDetail;
